package com.sanjeet.androidassignment.apiimport com.jakewharton.retrofit2.adapter.kotlin.coroutines.CoroutineCallAdapterFactoryimport okhttp3.OkHttpClientimport retrofit2.Retrofitimport retrofit2.converter.gson.GsonConverterFactoryobject RetrofitHelper {    private const val BASE_URL = "https://rickandmortyapi.com/api/"    val apiService: ApiInterface by lazy {        Retrofit.Builder()            .baseUrl(BASE_URL)            .addConverterFactory(GsonConverterFactory.create())            .client(OkHttpClient())            .addCallAdapterFactory(CoroutineCallAdapterFactory())            .build()            .create(ApiInterface::class.java)    }}