package com.sanjeet.androidassignment.data.characterModelimport androidx.room.ColumnInfoimport androidx.room.Entityimport androidx.room.PrimaryKey@Entity(tableName = "bookmarked_characters")data class CharacterList(    @ColumnInfo val created: String,    @ColumnInfo val episode: List<String>,    @ColumnInfo val gender: String,    @PrimaryKey val id: Int,    @ColumnInfo val image: String,    @ColumnInfo val location: Location,    @ColumnInfo val name: String,    @ColumnInfo val origin: Origin,    @ColumnInfo val species: String,    @ColumnInfo val status: String,    @ColumnInfo val type: String,    @ColumnInfo val url: String,    @ColumnInfo var episodeName: String?=null,    @ColumnInfo var isBookMarked: Boolean)