package com.sanjeet.androidassignment.data.dbimport androidx.room.Daoimport androidx.room.Deleteimport androidx.room.Insertimport androidx.room.OnConflictStrategyimport androidx.room.Queryimport com.sanjeet.androidassignment.data.characterModel.CharacterList@Daointerface BookMarDao {    @Query("SELECT * FROM bookmarked_characters")    suspend fun getAllBookMarkedCharacter(): MutableList<CharacterList>    @Insert(onConflict = OnConflictStrategy.REPLACE)    suspend fun addBookMarkedCharacter(character: CharacterList)    @Delete    suspend fun removeBookMarkedCharacter(character: CharacterList)    @Query("SELECT COUNT(*) > 0 FROM bookmarked_characters WHERE id = :id")    suspend fun isCharacterBookmarked(id: Int): Boolean}