package com.sanjeet.androidassignment.data.dbimport android.content.Contextimport androidx.room.Databaseimport androidx.room.Roomimport androidx.room.RoomDatabaseimport androidx.room.TypeConvertersimport com.sanjeet.androidassignment.data.characterModel.CharacterListimport com.sanjeet.androidassignment.utils.Converters@Database(entities = [CharacterList::class], version = 1)@TypeConverters(Converters::class)abstract class BookMarkDatabase : RoomDatabase() {    abstract fun bookMarkDao(): BookMarDao    companion object {        @Volatile        private var INSTANCE: BookMarkDatabase? = null        fun getDatabase(context: Context): BookMarkDatabase {            return INSTANCE ?: synchronized(this) {                val instance = Room.databaseBuilder(                    context.applicationContext,                    BookMarkDatabase::class.java,                    "user_database"                ).build()                INSTANCE = instance                instance            }        }    }}