package com.sanjeet.androidassignment.practicefun main() {    val ob = ClassOuter("Sanjeet")    ob.firstName()    ob.ClassInner("Kushwaha").lastName()}//A nested class marked as inner can access the members of its outer class.// Inner classes carry a reference to an object of an outer class:class ClassOuter(val firstName: String) {    inner class ClassInner(private val lastName: String) {        fun lastName() {            println(lastName)        }    }    fun firstName() {        println(firstName)    }}