package com.sanjeet.androidassignment.practice.oopfun main() {    val b = Box()    b.apply {        configShape()        buildingBox()        shapeQuality()    }}class Box : Shape() {    override fun configShape() {        println("building Box")    }    override fun cutShape() {        println("forming box shape")    }    override fun polishShape() {        println("polishing box")    }    fun buildingBox() {        println("building box")    }    override fun completed() {        println("building box finished")    }}abstract class Shape() {    abstract fun configShape()    abstract fun polishShape()    abstract fun cutShape()    abstract fun completed()    fun shapeQuality() {        println("shape quality is good")    }}