package com.sanjeet.androidassignment.practice.oop//Kotlin extension functions allow a developer to “add”// methods to a class without inheriting it or using a design patternfun main() {    val name = "Sanjeet Kushwaha"    println(name.getAllNameWords())    println(name.greetingLine("sdsfas"))}private fun String.greetingLine(time: String): String {    return when (time) {        "morning" -> "Good Morning ${this.split(" ").first()} "        "afternoon" -> "Good Afternoon ${this.split(" ").first()}"        else -> "Hello  ${this.split(" ").first()}"    }}fun String.getAllNameWords(): List<String> {    return this.split(" ")}