package com.sanjeet.androidassignment.practice.oopfun main() {    val videoName = "Chaiyaan Chaiyaan"    val video = Video(videoName)    video.apply {        configDevice()        play()        downloadFile(videoName)    }    val audioName = "Chammakk Challo"    val audio = Audio(audioName)    audio.apply {        configDevice()        play()        downloadFile(audioName)    }}abstract class Media : ConfigDevice, Downloader {    abstract fun play()}class Video(private var videoName: String) : Media() {    override fun configDevice() {        println("config video player")    }    override fun play() {        println("playing video $videoName")    }}class Audio(private var audioName: String) : Media() {    override fun configDevice() {        println("config audio player")    }    override fun play() {        println("playing audio $audioName")    }}interface ConfigDevice {    fun configDevice()}interface Downloader {    fun downloadFile(name: String) {        println("downloading $name")    }}