package com.sanjeet.androidassignment.ui.characterlistimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.bumptech.glide.Glideimport com.bumptech.glide.load.resource.drawable.DrawableTransitionOptionsimport com.sanjeet.androidassignment.data.characterModel.CharacterListimport com.sanjeet.androidassignment.databinding.CharacterListItemBindingimport timber.log.Timberclass CharacterListAdapter(characterLists: MutableList<CharacterList>) :    RecyclerView.Adapter<CharacterListViewHolder>() {    var charList = characterLists    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): CharacterListViewHolder {        return CharacterListViewHolder(            CharacterListItemBinding.inflate(                LayoutInflater.from(parent.context),                parent,                false            )        )    }    override fun getItemCount(): Int = charList.size    override fun onBindViewHolder(holder: CharacterListViewHolder, position: Int) {        holder.bindItems(charList[position])    }    fun updateAdapter(characterLists: MutableList<CharacterList>) {        this.charList = characterLists        notifyDataSetChanged()    }}class CharacterListViewHolder(private var binding: CharacterListItemBinding) :    RecyclerView.ViewHolder(binding.root) {    fun bindItems(list: CharacterList) {        Glide.with(binding.image.context).load(list.image).transition(DrawableTransitionOptions.withCrossFade()).into(binding.image);        binding.name.text = list.name        binding.allDetails.text = "${list.status}, ${list.species}, ${list.gender}"        binding.episodeName.text = list.episodeName    }}