package com.sanjeet.androidassignment.utilsimport androidx.room.TypeConverterimport com.google.gson.Gsonimport com.google.gson.reflect.TypeTokenimport com.sanjeet.androidassignment.data.characterModel.Locationimport com.sanjeet.androidassignment.data.characterModel.Originclass Converters {    @TypeConverter    fun fromEpisodeList(value: List<String>): String {        return Gson().toJson(value)    }    @TypeConverter    fun toEpisodeList(value: String): List<String> {        val listType = object : TypeToken<List<String>>() {}.type        return Gson().fromJson(value, listType)    }    @TypeConverter    fun fromLocation(location: Location): String {        return Gson().toJson(location)    }    @TypeConverter    fun toLocation(locationString: String): Location {        return Gson().fromJson(locationString, object : TypeToken<Location>() {}.type)    }    @TypeConverter    fun fromOrigin(origin: Origin): String {        return Gson().toJson(origin)    }    @TypeConverter    fun toOrigin(originString: String): Origin {        return Gson().fromJson(originString, object : TypeToken<Origin>() {}.type)    }}