package com.sanjeet.notesappimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.sanjeet.notesapp.database.NotesModelimport com.sanjeet.notesapp.databinding.NoteItemBindingclass NotesAdapter(notesList: List<NotesModel>) : RecyclerView.Adapter<NotesViewHolder>() {    val list = notesList    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): NotesViewHolder {        return NotesViewHolder(            NoteItemBinding.inflate(                LayoutInflater.from(parent.context),                parent,                false            )        )    }    override fun getItemCount(): Int = list.size    override fun onBindViewHolder(holder: NotesViewHolder, position: Int) {        holder.bindItems(list[position])    }}class NotesViewHolder(private var binding: NoteItemBinding) :    RecyclerView.ViewHolder(binding.root) {    fun bindItems(notesModel: NotesModel) {        binding.notesHeading.text = notesModel.noteTitle        binding.notesContent.text = notesModel.noteContent    }}