package com.sanjeet.notesapp.databaseimport androidx.room.Daoimport androidx.room.Deleteimport androidx.room.Insertimport androidx.room.OnConflictStrategyimport androidx.room.Query@Daointerface NoteDao {    @Query("SELECT * FROM notes")    fun getAll(): MutableList<NotesModel>    @Query("SELECT * FROM notes WHERE uid IN (:userIds)")    suspend fun loadAllByIds(userIds: IntArray): List<NotesModel>    @Query("SELECT * FROM notes WHERE title LIKE :title AND " +            "content LIKE :content LIMIT 1")    suspend fun findByWord(title: String, content: String): NotesModel    @Insert(onConflict = OnConflictStrategy.REPLACE)    suspend fun insertAll(notes: NotesModel): Long    @Delete    suspend fun delete(notes: NotesModel)}