package com.sanjeet.notesapp.databaseimport androidx.room.ColumnInfoimport androidx.room.Entityimport androidx.room.PrimaryKey@Entity(tableName = "notes")data class NotesModel(    @PrimaryKey(autoGenerate = true) val uid: Long? = null,    @ColumnInfo(name = "title") val noteTitle: String?,    @ColumnInfo(name = "content") val noteContent: String?,    @ColumnInfo(name = "date_time") val dateTime: String?)